<script>
export default {
  props: [
    '検査実施人数',
    '陽性物数',
    '入院中',
    '軽症中等症',
    '重症',
    '死亡',
    '退院'
  ],
  methods: {
    /** 桁数に応じて位置の調整をする */
    getAdjustX(input) {
      const length = input.toString(10).length
      switch (length) {
        case 1: {
          return 3
        }
        case 2: {
          return 0
        }
        case 3: {
          return -3
        }
        case 4: {
          return -8
        }
        default: {
          return 0
        }
      }
    },
    /** グラフ内容がわかる支援技術用テキストの中身を取得する **/
    ariaLabel(
      inspected,
      positive,
      hospitalized,
      mild,
      critically,
      deceased,
      discharged
    ) {
      const ariaLabel = `検査陽性者の状況: 検査実施人数は${inspected}人、うち累積の陽性者数は${positive}人です。入院中は${hospitalized}人で、うち軽症・中等症は${mild}人、また重症は${critically}人です。さらに死亡は${deceased}人、退院は${discharged}人です。`
      return ariaLabel
    }
  }
}
</script>

